import { useSelector,useDispatch } from "react-redux";
import {useEffect} from 'react';
import { Link , useParams} from "react-router-dom";
import {
  HeartIcon,
  EmojiSadIcon,
  UploadIcon,
  UserIcon,
} from "@heroicons/react/outline";

import { MailIcon, EyeIcon } from "@heroicons/react/solid";
import { userProfileAction } from "../../../redux/slices/users/usersSlices";
import { DateFormatter } from "../../../utils/DateFormatter";







export default function Profile() {

 const {id}=useParams();
 const dispatch = useDispatch();
 //console.log(id)
 //fetch user profile
 useEffect(() => {dispatch(userProfileAction(id));},[id,dispatch]); 
//user data from storeData
const users=useSelector(state=>state.users);
console.log(users);
const {profile, loading, appErr, serverErr}=users;
console.log(profile, loading, appErr, serverErr);
  return (
    <>
      <div className="h-screen flex overflow-hidden bg-white">
        {/* Static sidebar for desktop */}

        <div className="flex flex-col min-w-0 flex-1 overflow-hidden">
          <div className="flex-1 relative z-0 flex overflow-hidden">
            <main className="flex-1 relative z-0 overflow-y-auto focus:outline-none xl:order-last">
              <article>
                {/* Profile header */}
                <div>
                  <div>
                    <img
                      className="h-32 w-full object-cover lg:h-48"
                      src={profile?.profilePhoto} 
                      alt={profile?.firstName}
                    />
                  </div>
                  <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="-mt-12 sm:-mt-16 sm:flex sm:items-end sm:space-x-5">
                      <div className="flex -mt-20">
                        <img
                          className="h-24 w-24 rounded-full  ring-4 ring-white sm:h-32 sm:w-32"
                           src={profile?.profilePhoto}
                           alt={profile?.firstName}
                        />
                      </div>
                      <div className="mt-6 sm:flex-1 sm:min-w-0 sm:flex sm:items-center sm:justify-end sm:space-x-6 sm:pb-1">
                        <div className=" flex flex-col 2xl:block mt-10 min-w-0 flex-1">
                          <h1 className="text-2xl font-bold text-gray-900 ">
                             {profile?.firstName} {profile?.lastName}

                            
                          </h1>
                          <p className="m-3 text-lg">
                            Date Joined:
                             <DateFormatter date={profile?.createdAt} />{" "} 
                          </p>
                          <p className="text-green-600 mt-2 mb-2">
                             {profile?.posts.length} posts{" "}
                            {profile?.followers.length} followers{" "}
                            {profile?.following.length} following 
                          </p>
                         

                          {/* is login user */}
                          {/* Upload profile photo */}
                          <Link to=""
                            // to={`/upload-profile-photo/${profile?._id}`}
                            className="inline-flex justify-center w-48 px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-pink-500"
                          >
                            <UploadIcon
                              className="-ml-1 mr-2 h-5 w-5 text-gray-400"
                              aria-hidden="true"
                            />
                            <span>Upload Photo</span>
                          </Link>
                        </div>

                        <div className="mt-6 flex flex-col justify-stretch space-y-3 sm:flex-row sm:space-y-0 sm:space-x-4">
                          {/* // Hide follow button from the same */}
                          <div>
                            <button
                              // onClick={() =>
                              //   dispatch(unFollowUserAction(profile?._id))
                              // }
                              className="inline-flex justify-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-pink-500"
                            >
                              <EmojiSadIcon
                                className="-ml-1 mr-2 h-5 w-5 text-gray-400"
                                aria-hidden="true"
                              />
                              <span>Unfollow</span>
                            </button>

                            <>
                              <button
                               // onClick={()=>{dispatch(followUserAction(id))}}
                                type="button"
                                className="inline-flex justify-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-pink-500"
                              >
                                <HeartIcon
                                  className="-ml-1 mr-2 h-5 w-5 text-gray-400"
                                  aria-hidden="true"
                                />
                                <span>Follow </span>
                              </button>
                            </>
                          </div>

                          {/* Update Profile */}

                          <>
                            <Link
                              to=""
                              className="inline-flex justify-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-pink-500"
                            >
                              <UserIcon
                                className="-ml-1 mr-2 h-5 w-5 text-gray-400"
                                aria-hidden="true"
                              />
                              <span>Update Profile</span>
                            </Link>
                          </>
                          {/* Send Mail */}
                          <Link to=""
                            // to={`/send-mail?email=${profile?.email}`}
                            className="inline-flex justify-center bg-indigo-900 px-4 py-2 border border-yellow-700 shadow-sm text-sm font-medium rounded-md text-gray-700  hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-pink-500"
                          >
                            <MailIcon
                              className="-ml-1 mr-2 h-5 w-5 text-gray-200"
                              aria-hidden="true"
                            />
                            <span className="text-base mr-2  text-bold text-yellow-500">
                              Send Message
                            </span>
                          </Link>
                        </div>
                      </div>
                    </div>
                    <div className="hidden sm:block 2xl:hidden mt-6 min-w-0 flex-1">
                      <h1 className="text-2xl font-bold text-gray-900 truncate">
                       {profile?.firstName}  {profile?.lastName} 
                      </h1>
                    </div>
                  </div>
                </div>
                {/* Tabs */}
                <div className="mt-6 sm:mt-2 2xl:mt-5">
                  <div className="border-b border-red-900">
                    <div className="max-w-5xl mx-auto "></div>
                  </div>
                </div>
                <div className="flex justify-center place-items-start flex-wrap  md:mb-0">
                 
                  {/* All my Post */}
                  <div className="w-full md:w-2/3 px-4 mb-4 md:mb-0">
                    <h1 className="text-center text-xl border-gray-500 mb-2 border-b-2">
                      My Post
                    </h1>
                    {/* Look here */}
                    {profile?.posts?.length <=0 ? <h2 className="text-center text-xl">No post</h2>:
                    profile?.posts?.map(post=>(<div className="flex flex-wrap  -mx-3 mt-3  lg:mb-6">
                    <div className="mb-2   w-full lg:w-1/4 px-3">
                      <Link to="">
                        <img
                          className="object-cover h-40 rounded"
                           src={post?.image}
                          alt="poster"
                        />
                      </Link>
                    </div>
                    <div className="w-full lg:w-3/4 px-3">
                      <Link 
                        to={`/post/${post?._id}`}
                        className="hover:underline"
                      >
                        <h3 className="mb-1 text-2xl text-green-400 font-bold font-heading">
                          {post?.title}
                        </h3>
                      </Link>
                      <p className="text-gray-600 truncate">
                       {post?.description}
                      </p>
                      {/* Read more */}
                      <Link to=""
                        className="text-indigo-500 hover:underline"
                        // to={`/post/${post?._id}`}
                      ></Link>
                    </div>
                  </div>))}
                  </div>
                </div>
              </article>
            </main>
          </div>
        </div>
      </div>
    </>
  );
}
